#import <Specta/Specta.h>
#import <Expecta/Expecta.h>

#import <easeqs_objc/easeqs-objc-umbrella.h>

#import <objc/runtime.h>

static NSString * const ESQEasingFunctionTest = @"Easing Function Test";

static NSString * const ESQEasingFunctionKey = @"easingFunction";
static NSString * const ESQEasingFunctionPointerKey = @"easingFunctionPointer";
static NSString * const ESQEasingFunctionDefaultParametersKey = @"easingFunctionDefaultParameters";
static NSString * const ESQEasingFunctionTypeKey = @"easingFunctionType";
static NSString * const ESQEasingFunctionValuesKey = @"easingFunctionValues";

static const CGFloat ESQMinimumProgress = 0.f;
static const CGFloat ESQMaximumProgress = 1.f;

static const CGFloat ESQProgressStep = .01f;

NS_INLINE CGFloat callEasingFuncitonWithArray(ESQEasingCFunction easingFunction, CGFloat progress, NSArray<NSNumber *> *parameters) {
    CGFloat result = 0;
    if (parameters.count == 1) {
        result = easingFunction(progress, parameters[0].floatValue, nil);
    } else if (parameters.count == 2) {
        result = easingFunction(progress, parameters[0].floatValue, parameters[1].floatValue, nil);
    } else {
        result = easingFunction(progress, nil);
    }
    return result;
}

NS_INLINE NSNumber *roundedNumberFromFloatUsingFormatter(CGFloat inputValue, NSNumberFormatter *numberFormatter) {
    NSString *stringRepresentation = [numberFormatter stringFromNumber:@(inputValue)];
    NSNumber *value = [numberFormatter numberFromString:stringRepresentation];
    return value;
}

#pragma mark - Shared

SharedExamplesBegin(ESQEasingFunction)

sharedExamplesFor(ESQEasingFunctionTest, ^(NSDictionary *data) {
    
    ESQEasingFunction *easingFunction = data[ESQEasingFunctionKey];
    NSNumber *easingFunctionType = data[ESQEasingFunctionTypeKey];
    NSArray<NSNumber *> *easingFunctionInputValues = data[ESQEasingFunctionValuesKey];
    ESQEasingCFunction easingCFunction = [data[ESQEasingFunctionPointerKey] pointerValue];
    NSArray *easingFunctionDefaultParameters = data[ESQEasingFunctionDefaultParametersKey] ?: [[NSArray alloc] init];
    
    describe(@"this", ^{
        it(@"should exist", ^{
            expect(easingFunction).toNot.beNil();
        });
        
        if (easingFunctionType) {
            it(@"should have specified type when it is", ^{
                expect(easingFunction.type).to.equal(easingFunctionType.unsignedIntegerValue);
            });
            
            if (easingFunctionInputValues) {
                it(@"should be subclass of ESQEasingFunction", ^{
                    expect(easingFunction.class).to.beSubclassOf(ESQEasingFunction.class);
                });
                
                NSMutableArray *easedValues = [[NSMutableArray alloc] init];
                NSMutableArray *easedValuesUsingFunciton = [[NSMutableArray alloc] init];
                NSNumberFormatter *numberFormatter = [[NSNumberFormatter alloc] init];
                numberFormatter.numberStyle = NSNumberFormatterDecimalStyle;
                numberFormatter.maximumFractionDigits = 15;
                numberFormatter.roundingMode = NSNumberFormatterRoundHalfUp;
                for (CGFloat progress = ESQMinimumProgress; progress <= ESQMaximumProgress; progress += ESQProgressStep) {
                    if (progress + ESQProgressStep > ESQMaximumProgress) {
                        progress = ESQMaximumProgress;
                    }
                    
                    {
                        CGFloat easedProgress = [easingFunction easedProgress:progress];
                        [easedValues addObject:roundedNumberFromFloatUsingFormatter(easedProgress, numberFormatter)];
                    }
                    
                    if (easingCFunction) {
                        CGFloat easedProgress = callEasingFuncitonWithArray(easingCFunction, progress, easingFunctionDefaultParameters);
                        [easedValuesUsingFunciton addObject:roundedNumberFromFloatUsingFormatter(easedProgress, numberFormatter)];
                    }
                }
                
                it(@"should match all values from easing function to input values", ^{
                    expect(easedValues).to.equal(easingFunctionInputValues);
                });
                
                if (easingCFunction) {                                                                                                                                                                                                                                                                                                                                                                                                                
                    it(@"should match all values from easing c function to input values", ^{
                        expect(easedValuesUsingFunciton).to.equal(easingFunctionInputValues);
                    });
                }
                
                it(@"should return minimum progress eased value when minimum progress value passed", ^{
                    expect(@([easingFunction easedProgress:ESQMinimumProgress])).to.equal(@(ESQMinimumProgress));
                });
                
                it(@"should return maximum progress eased value when maximum progress value passed", ^{
                    expect(@([easingFunction easedProgress:ESQMaximumProgress])).to.equal(@(ESQMaximumProgress));
                });
            }
        } else {
            it(@"should have default type when it not specified", ^{
                expect(easingFunction.type).to.equal(easingFunction.defaultType);
            });
            
            it(@"should generate exception when calling easedProgress: selector", ^{
                @try {
                    [easingFunction easedProgress:ESQMinimumProgress];
                    failure(@"easedProgress: should cause exception");
                } @catch (NSException *exception) {
                    // It's okay, this method should fail for root class
                }
            });
        }
    });
    
});

SharedExamplesEnd

#pragma mark - Tests

SpecBegin(ESQEasingFunction)

#pragma mark - All

describe(@"All easing funcitons should have a description", ^{
    int numberOfClasses = objc_getClassList(NULL, 0);
    Class *classes = (__unsafe_unretained Class *) malloc(sizeof(Class) * numberOfClasses);
    numberOfClasses = objc_getClassList(classes, numberOfClasses);
    
    for (NSUInteger i = 0; i < numberOfClasses; i++) {
        Class class = classes[i];
        Class superclass = class;
        do {
            superclass = class_getSuperclass(superclass);
        } while (superclass && ESQEasingFunction.class != superclass);
        
        if (superclass) {
            expect([class description]).notTo.beEmpty();
        }
    }
    free(classes);
});

